INSERT INTO `messages` VALUES (1,73,58,'----------------------------------------------------------------------',0,'2020-07-20 22:50:41'),(2,84,89,'РОМАН В СТИХАХ',1,'2020-07-20 22:50:41'),(3,48,76,'avouer  avec  la  meme  indifference les bonnes comme les mauvaises actions,',0,'2020-07-20 22:50:41'),(4,69,86,'suite d\'un sentiment de superiorite peut-etre imaginaire.',0,'2020-07-20 22:50:41'),(5,52,73,'Tire d\'une lettre particuliere.',1,'2020-07-20 22:50:41'),(6,97,70,'Залог достойнее тебя,',0,'2020-07-20 22:50:41'),(7,78,74,'Высоких дум и простоты;',0,'2020-07-20 22:50:41'),(8,31,72,'Простонародных, идеальных,',1,'2020-07-20 22:50:41'),(9,84,46,'Небрежный плод моих забав,',0,'2020-07-20 22:50:41'),(10,99,44,'Бессонниц, легких вдохновений,',0,'2020-07-20 22:50:41'),(11,80,97,'И сердца горестных замет.',1,'2020-07-20 22:50:41'),(12,68,86,'I',1,'2020-07-20 22:50:41'),(13,71,70,'\"Мой дядя самых честных правил,',1,'2020-07-20 22:50:41'),(14,83,85,'Он уважать себя заставил',0,'2020-07-20 22:50:41'),(15,80,32,'Не отходя ни шагу прочь!',1,'2020-07-20 22:50:41'),(16,75,72,'Полуживого забавлять,',0,'2020-07-20 22:50:41'),(17,97,43,'Печально подносить лекарство,',1,'2020-07-20 22:50:41'),(18,59,67,'Вздыхать и думать про себя:',1,'2020-07-20 22:50:41'),(19,67,64,'Всевышней волею Зевеса',1,'2020-07-20 22:50:41'),(20,67,66,'Друзья Людмилы и Руслана!',1,'2020-07-20 22:50:41'),(21,74,61,'Где, может быть, родились вы',1,'2020-07-20 22:50:41'),(22,34,35,'Служив отлично благородно,',0,'2020-07-20 22:50:41'),(23,94,54,'Судьба Евгения хранила:',1,'2020-07-20 22:50:41'),(24,40,57,'Потом Monsieur ее сменил.',1,'2020-07-20 22:50:41'),(25,41,40,'Ребенок был резов, но мил.',0,'2020-07-20 22:50:41'),(26,83,70,'Учил его всему шутя,',1,'2020-07-20 22:50:41'),(27,47,56,'И в Летний сад гулять водил.',0,'2020-07-20 22:50:41'),(28,80,44,'IV',1,'2020-07-20 22:50:41'),(29,93,62,'Когда же юности мятежной',1,'2020-07-20 22:50:41'),(30,39,99,'Пришла Евгению пора,',1,'2020-07-20 22:50:41'),(31,65,40,'